formulas.expression:
AND(
{!$User.Department} != "Quality Assurance",
NOT({!$Permission.Admin_Validation_Rule_Override}),
OR(
ISPICKVAL({!GetEvent.Type_of_Appointment__c}, 'Tax Savings Analysis') ,
ISPICKVAL({!GetEvent.Type_of_Appointment__c}, 'Formation Consultation' ),
ISPICKVAL({!GetEvent.Type_of_Appointment__c}, 'BNA') ,
ISPICKVAL({!GetEvent.Type_of_Appointment__c} , 'Calendar Block')
),
/*Error if Disposition field values are changed*/
OR(
TEXT( {!GetEvent.Appointment_Disposition__c}) != {!Disposition_Fields.middleValue} ,
TEXT ({!GetEvent.Secondary_Disposition_Details__c}) != {!Disposition_Fields.bottomValue}
),
/* Error if more than 3 days since Friday or more than 1 day for other days*/
IF(WEEKDAY({!GetEvent.ActivityDate})=6, (TODAY()- {!GetEvent.ActivityDate})>3 , (TODAY()- {!GetEvent.ActivityDate})>1),

/*Exception if User is Sales Manager or SDR Manager and upto a month since event date*/
NOT (AND(
OR(
{!$User.ProfileId} = '00ej0000000mMPU',
{!$UserRole.Name} = 'SDR Manager'
),
(TODAY()-{!GetEvent.ActivityDate} <=30 )
))
)

AND(
/*Error if a TSA that is more than 5 minutes in future is Dispositioned*/
ISPICKVAL( {!GetEvent.Type_of_Appointment__c} , 'Tax Savings Analysis'),
OR(
TEXT( {!GetEvent.Appointment_Disposition__c}) != {!Disposition_Fields.middleValue} ,
TEXT ({!GetEvent.Secondary_Disposition_Details__c}) != {!Disposition_Fields.bottomValue}
),
AND(
{!GetEvent.Appt_Time__c} > NOW(),
{!GetEvent.Appt_Time__c} - NOW()> 5/1440),

/*Exception - Admins, SDR Manager, SDRs */
NOT( {!$Permission.Admin_Validation_Rule_Override}),
NOT( {!$Profile.Name} = 'SDR Manager'),
NOT( {!$Profile.Name} = 'Appointment Setter')
)

AND(
NOT(
OR( {!$Permission.Admin_Validation_Rule_Override},
{!$User.Department} = 'Quality Assurance')),
OR(
/*Event Disposition is set to Failure to call*/
{!Disposition_Fields.middleValue} = 'Failure to Call',
AND (
/* Event disposition is changed from Failure to call*/
TEXT({!GetEvent.Appointment_Disposition__c}) = 'Failure to Call',
TEXT({!GetEvent.Appointment_Disposition__c}) != {!Disposition_Fields.middleValue}
)
))

AND(
OR(
{!Disposition_Fields.bottomValue} = "Not Interested",
{!Disposition_Fields.bottomValue} = "Cancelled"
),
OR (
AND ( {!Contact} , ISPICKVAL({!Get_Account.Rejection_Reason__c}, "") ) , 
AND ( {!Lead},  ISPICKVAL({!Get_Lead.Rejection_Reason__c}, "") )
)
)

/* Returns var_Campaign_Values Campaign Picklist text string without "SDR Live Transfer" for lead or account. */
/* Formula below Substitutes "SDR Live Transfer;" (Incase of multiple values) or "SDR Live Transfer" with null*/
IF ( {!Lead} , 
SUBSTITUTE(SUBSTITUTE({!var_Campaign_Values}, 'SDR Live Transfer;', null), 'SDR Live Transfer', null) , 
SUBSTITUTE(SUBSTITUTE({!var_Campaign_Values}, 'SDR Live Transfer;', null), 'SDR Live Transfer', null)
)
